    private function processDistricts($districts, $division)
    {
        foreach ($districts as $districtName => $upazilas) {
            // জেলা তৈরি করুন
            $district = BangladeshAddress::create([
                'type' => 'district',
                'name_bn' => $districtName,
                'name_en' => null,
                'parent_id' => $division->id
            ]);

            echo "  Imported District: {$districtName}\n";

            foreach ($upazilas as $upazilaName => $unions) {
                // উপজেলা তৈরি করুন
                $upazila = BangladeshAddress::create([
                    'type' => 'upazila',
                    'name_bn' => $upazilaName,
                    'name_en' => null,
                    'parent_id' => $district->id
                ]);

                echo "    Imported Upazila: {$upazilaName}\n";

                foreach ($unions as $unionName) {
                    // ইউনিয়ন তৈরি করুন
                    BangladeshAddress::create([
                        'type' => 'union',
                        'name_bn' => trim($unionName),
                        'name_en' => null,
                        'parent_id' => $upazila->id
                    ]);
                }

                echo "      Imported {$upazilaName}: " . count($unions) . " unions\n";
            }
        }
    }
